import React from 'react'
import { Link } from "react-router-dom";
import constants from '../constants'

const { API_URL } = constants

class Movie extends React.Component {
    componentDidMount() {
        this.video.addEventListener('error', () => {
            console.log('OOOOPS I GOT IT!')
        })
    }

    componentWillMount() {
        this.video.removeEventListener('error', () => {
            console.log('OOOOPS REMOVED!')
        })
    }

    render() {
        const { match } = this.props;
        const { id } = match.params
        return <div>
            <video 
                ref= {elem => this.video = elem}
                controls id="videoPlayer" 
                width="620" 
                autoPlay={true}
                height="400">
                <source src={API_URL + '/list/' + id} type="video/mp4"></source>
            </video>
            <div className='btn-back'>
                <button><Link to='/movies'>Back</Link> </button>
            </div>
        </div>
    }
}

export default Movie
