import React from 'react'
import { Link } from "react-router-dom";
import constants from '../constants'

const { API_URL } = constants

class Movie extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loadedFailed: false
        }
        this.renderVideo = this.renderVideo.bind(this)
    }
    onError() {
        this.setState({ loadedFailed: true })
    }
    renderVideo = ()=> {
        const { match } = this.props;
        const { id } = match.params;

        if (this.state.loadedFailed) {
            return <div className='message-error'>Video Failed to load</div>
        } else {
            return <video
                controls
                id="videoPlayer"
                width="620"
                autoPlay={true}
                height="400">
                <source onError={this.onError} src={API_URL + '/list/' + id} type="video/mp4"></source>
            </video>
        }
    }
    render() {
        return <div>
            {this.renderVideo()}
            <div className='btn-back'>
                <button><Link to='/movies'>Back</Link> </button>
            </div>
        </div>
    }
}

export default Movie
